import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';
import { useTranslation } from 'react-i18next';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

export default function Analytics() {
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const { t } = useTranslation();

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/analytics`, {
        headers: {
          'x-auth-token': token,
        },
      });
      setAnalytics(res.data);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch analytics');
      setLoading(false);
    }
  };

  // Prepare chart data
  const topicLabels = analytics?.topicsCovered || [];
  const topicCounts = analytics?.topicPreferences?.map(t => t.count) || [];
  const topicNames = analytics?.topicPreferences?.map(t => t.name) || [];

  const barData = {
    labels: topicNames,
    datasets: [
      {
        label: t('Articles Read'),
        data: topicCounts,
        backgroundColor: 'rgba(59,130,246,0.7)',
      },
    ],
  };

  const pieData = {
    labels: topicNames,
    datasets: [
      {
        label: t('Articles by Topic'),
        data: topicCounts,
        backgroundColor: [
          '#fbbf24', '#60a5fa', '#34d399', '#f87171', '#a78bfa', '#f472b6', '#facc15', '#38bdf8'
        ],
      },
    ],
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-2xl font-bold text-gray-900 mb-8">{t('Reading Analytics')}</h1>

      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {/* Reading Stats */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4">{t('Reading Statistics')}</h2>
          <div className="space-y-4">
            <div>
              <p className="text-sm text-gray-500">{t('Total Articles Read')}</p>
              <p className="text-2xl font-semibold text-gray-900">{analytics?.totalArticlesRead || 0}</p>
            </div>
            <div>
              <p className="text-sm text-gray-500">{t('Total Time Spent')}</p>
              <p className="text-2xl font-semibold text-gray-900">{analytics?.totalTimeSpent || 0} {t('min')}</p>
            </div>
            <div>
              <p className="text-sm text-gray-500">{t('Articles Saved')}</p>
              <p className="text-2xl font-semibold text-gray-900">{analytics?.savedArticles || 0}</p>
            </div>
          </div>
        </div>

        {/* Topic Preferences Bar Chart */}
        <div className="bg-white rounded-lg shadow-md p-6 flex flex-col">
          <h2 className="text-lg font-medium text-gray-900 mb-4">{t('Topic Preferences')}</h2>
          {topicNames.length > 0 ? (
            <Bar data={barData} options={{
              responsive: true,
              plugins: { legend: { display: false }, title: { display: false } },
              scales: { y: { beginAtZero: true } }
            }} />
          ) : (
            <p className="text-gray-500">{t('No topic data yet.')}</p>
          )}
        </div>

        {/* Topic Distribution Pie Chart */}
        <div className="bg-white rounded-lg shadow-md p-6 flex flex-col">
          <h2 className="text-lg font-medium text-gray-900 mb-4">{t('Topic Distribution')}</h2>
          {topicNames.length > 0 ? (
            <Pie data={pieData} options={{
              responsive: true,
              plugins: { legend: { position: 'bottom' }, title: { display: false } }
            }} />
          ) : (
            <p className="text-gray-500">{t('No topic data yet.')}</p>
          )}
        </div>
      </div>

      {/* Reading Insights */}
      <div className="mt-8 bg-white rounded-lg shadow-md p-6">
        <h2 className="text-lg font-medium text-gray-900 mb-4">{t('Reading Insights')}</h2>
        <div className="space-y-4">
          {analytics?.insights?.map((insight, index) => (
            <div key={index} className="flex items-start">
              <div className="flex-shrink-0">
                <div className="h-8 w-8 rounded-full bg-primary-100 flex items-center justify-center">
                  <span className="text-primary-600 text-sm font-medium">{index + 1}</span>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-900">{insight}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
