import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { BookmarkIcon, ChatBubbleLeftIcon, HeartIcon, ChatBubbleOvalLeftEllipsisIcon, FireIcon } from '@heroicons/react/24/outline';
import { io } from 'socket.io-client';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

export default function Home() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  // Search state
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [searchSummary, setSearchSummary] = useState('');
  const [searching, setSearching] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [userId, setUserId] = useState(null);
  const [trending, setTrending] = useState([]);
  const [category, setCategory] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  useEffect(() => {
    fetchArticles();
  }, []);
  
  useEffect(() => {
    const socket = io('http://localhost:5000'); // or your backend URL

    socket.on('new-article', (article) => {
      setArticles((prev) => [article, ...prev]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  useEffect(() => {
    const fetchNotifications = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/notifications`, {
        headers: { 'x-auth-token': token }
      });
      setNotifications(res.data);
    };
    fetchNotifications();
  }, []);

  useEffect(() => {
    // Get user ID from token (assumes JWT with sub or id field)
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUserId(payload.id || payload.sub || null);
      } catch (e) {
        setUserId(null);
      }
    }
  }, []);

  useEffect(() => {
    // Fetch trending articles
    const fetchTrending = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/trending`, {
          headers: { 'x-auth-token': token },
        });
        setTrending(res.data);
      } catch (err) {
        // Optionally toast error
      }
    };
    fetchTrending();
  }, []);

  const fetchArticles = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setLoading(false);
        return;
      }

      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed`, {
        headers: {
          'x-auth-token': token,
        },
      });
      setArticles(res.data);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch articles');
      setLoading(false);
    }
  };

  const handleSaveArticle = async (articleId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/news/article/${articleId}/save`,
        {},
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      toast.success('Article saved successfully');
    } catch (err) {
      toast.error('Failed to save article');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/qa`,
        {
          question,
          context: selectedArticle.content,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      setAnswer(res.data.answer);
    } catch (err) {
      toast.error('Failed to get answer');
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearching(true);
    try {
      const token = localStorage.getItem('token');
      const params = new URLSearchParams({ q: query });
      if (category) params.append('category', category);
      if (mediaType) params.append('mediaType', mediaType);
      if (startDate) params.append('startDate', startDate.toISOString());
      if (endDate) params.append('endDate', endDate.toISOString());
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/news/search?${params.toString()}`,
        { headers: { 'x-auth-token': token } }
      );
      setSearchResults(res.data.articles);
      setSearchSummary(res.data.summary);
    } catch (err) {
      toast.error('Failed to search');
    } finally {
      setSearching(false);
    }
  };

  const handleBackToFeed = () => {
    setSearchResults(null);
    setSearchSummary('');
    setQuery('');
  };

  const handleLikeArticle = async (articleId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/news/article/${articleId}/like`,
        {},
        { headers: { 'x-auth-token': token } }
      );
      setArticles((prev) =>
        prev.map((a) =>
          a._id === articleId ? { ...a, likes: res.data.likes } : a
        )
      );
      if (searchResults) {
        setSearchResults((prev) =>
          prev.map((a) =>
            a._id === articleId ? { ...a, likes: res.data.likes } : a
          )
        );
      }
    } catch (err) {
      toast.error('Failed to like article');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      {/* Search Bar */}
      <form onSubmit={handleSearch} className="mb-6 flex flex-col md:flex-row md:items-end gap-2 md:gap-4">
        <input
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search news..."
          className="flex-1 rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
        />
        <select
          value={category}
          onChange={e => setCategory(e.target.value)}
          className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
        >
          <option value="">All Categories</option>
          <option value="technology">Technology</option>
          <option value="science">Science</option>
          <option value="business">Business</option>
          <option value="health">Health</option>
          <option value="entertainment">Entertainment</option>
          <option value="sports">Sports</option>
          <option value="politics">Politics</option>
          <option value="education">Education</option>
        </select>
        <select
          value={mediaType}
          onChange={e => setMediaType(e.target.value)}
          className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
        >
          <option value="">All Media</option>
          <option value="text">Text</option>
          <option value="image">Image</option>
          <option value="video">Video</option>
          <option value="podcast">Podcast</option>
        </select>
        <div className="flex gap-2">
          <DatePicker
            selected={startDate}
            onChange={date => setStartDate(date)}
            selectsStart
            startDate={startDate}
            endDate={endDate}
            placeholderText="Start Date"
            className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 w-32"
            dateFormat="yyyy-MM-dd"
            isClearable
          />
          <DatePicker
            selected={endDate}
            onChange={date => setEndDate(date)}
            selectsEnd
            startDate={startDate}
            endDate={endDate}
            minDate={startDate}
            placeholderText="End Date"
            className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 w-32"
            dateFormat="yyyy-MM-dd"
            isClearable
          />
        </div>
        <button
          type="submit"
          className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700"
          disabled={searching}
        >
          {searching ? 'Searching...' : 'Search'}
        </button>
        {searchResults && (
          <button
            type="button"
            onClick={handleBackToFeed}
            className="px-4 py-2 rounded-md bg-gray-200 text-gray-700 font-medium hover:bg-gray-300"
          >
            Back to Feed
          </button>
        )}
      </form>

      {/* Show search results if searching, else show feed */}
      {searchResults ? (
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Search Results</h2>
          {searchSummary && (
            <div className="mb-4 p-4 bg-blue-50 rounded-md">
              <h4 className="font-medium text-blue-900 mb-2">AI Summary:</h4>
              <p className="text-blue-800">{searchSummary}</p>
            </div>
          )}
          <div className="space-y-6">
            {searchResults.length === 0 ? (
              <p className="text-gray-500">No articles found.</p>
            ) : (
              searchResults.map((article) => (
                <div
                  key={article._id}
                  className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
                >
                  {article.imageUrl && (
                    <img
                      src={article.imageUrl}
                      alt={article.title}
                      className="w-full h-48 object-cover"
                    />
                  )}
                  {article.videoUrl && (
                    <div className="my-4">
                      <iframe
                        width="100%"
                        height="315"
                        src={article.videoUrl}
                        title="Video"
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowFullScreen
                      ></iframe>
                    </div>
                  )}
                  {article.podcastUrl && (
                    <div className="my-4">
                      <iframe
                        src={article.podcastUrl}
                        width="100%"
                        height="152"
                        frameBorder="0"
                        allow="autoplay; clipboard-write; encrypted-media; picture-in-picture"
                        allowFullScreen
                        title="Podcast"
                      ></iframe>
                    </div>
                  )}
                  <div className="p-6">
                    <div className="flex items-center justify-between mb-4">
                      <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-medium bg-primary-100 text-primary-800">
                        {article.category}
                      </span>
                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => handleLikeArticle(article._id)}
                          className={`text-sm flex items-center gap-1 ${userId && article.likes && article.likes.includes(userId) ? 'text-red-500' : 'text-gray-400'} hover:text-red-600`}
                          title={userId && article.likes && article.likes.includes(userId) ? 'Unlike' : 'Like'}
                        >
                          <HeartIcon className="h-5 w-5" />
                          {article.likes ? article.likes.length : 0}
                        </button>
                        <button
                          onClick={() => handleSaveArticle(article._id)}
                          className="text-gray-400 hover:text-primary-600"
                        >
                          <BookmarkIcon className="h-6 w-6" />
                        </button>
                      </div>
                    </div>
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">
                      <Link to={`/article/${article._id}`} className="hover:text-primary-600">
                        {article.title}
                      </Link>
                    </h3>
                    <p className="text-gray-600 mb-4">{article.summary}</p>
                    <span className="text-sm text-gray-500">
                      {new Date(article.publishedAt).toLocaleDateString()}
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Articles Feed */}
          <div className="lg:col-span-2">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Your Personalized Feed</h2>
            <div className="space-y-6">
              {articles.map((article) => (
                <div
                  key={article._id}
                  className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
                >
                  {article.imageUrl && (
                    <img
                      src={article.imageUrl}
                      alt={article.title}
                      className="w-full h-48 object-cover"
                    />
                  )}
                  {article.videoUrl && (
                    <div className="my-4">
                      <iframe
                        width="100%"
                        height="315"
                        src={article.videoUrl}
                        title="Video"
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowFullScreen
                      ></iframe>
                    </div>
                  )}
                  {article.podcastUrl && (
                    <div className="my-4">
                      <iframe
                        src={article.podcastUrl}
                        width="100%"
                        height="152"
                        frameBorder="0"
                        allow="autoplay; clipboard-write; encrypted-media; picture-in-picture"
                        allowFullScreen
                        title="Podcast"
                      ></iframe>
                    </div>
                  )}
                  <div className="p-6">
                    <div className="flex items-center justify-between mb-4">
                      <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-medium bg-primary-100 text-primary-800">
                        {article.category}
                      </span>
                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => handleLikeArticle(article._id)}
                          className={`text-sm flex items-center gap-1 ${userId && article.likes && article.likes.includes(userId) ? 'text-red-500' : 'text-gray-400'} hover:text-red-600`}
                          title={userId && article.likes && article.likes.includes(userId) ? 'Unlike' : 'Like'}
                        >
                          <HeartIcon className="h-5 w-5" />
                          {article.likes ? article.likes.length : 0}
                        </button>
                        <button
                          onClick={() => handleSaveArticle(article._id)}
                          className="text-gray-400 hover:text-primary-600"
                        >
                          <BookmarkIcon className="h-6 w-6" />
                        </button>
                      </div>
                    </div>
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">
                      <Link to={`/article/${article._id}`} className="hover:text-primary-600">
                        {article.title}
                      </Link>
                    </h3>
                    <p className="text-gray-600 mb-4">{article.summary}</p>
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-gray-500">
                        {new Date(article.publishedAt).toLocaleDateString()}
                      </span>
                      <button
                        onClick={() => setSelectedArticle(article)}
                        className="inline-flex items-center text-sm text-primary-600 hover:text-primary-700"
                      >
                        <ChatBubbleLeftIcon className="h-5 w-5 mr-1" />
                        Ask AI
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* AI Assistant Panel */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-md p-6 sticky top-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">AI Assistant</h3>
              {selectedArticle ? (
                <div>
                  <h4 className="font-medium text-gray-900 mb-2">{selectedArticle.title}</h4>
                  <form onSubmit={handleAskQuestion} className="space-y-4">
                    <div>
                      <label htmlFor="question" className="block text-sm font-medium text-gray-700">
                        Ask a question about this article
                      </label>
                      <textarea
                        id="question"
                        rows={3}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        placeholder="What would you like to know?"
                      />
                    </div>
                    <button
                      type="submit"
                      className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                    >
                      Ask AI
                    </button>
                  </form>
                  {answer && (
                    <div className="mt-4 p-4 bg-gray-50 rounded-md">
                      <h5 className="font-medium text-gray-900 mb-2">AI Response:</h5>
                      <p className="text-gray-600">{answer}</p>
                    </div>
                  )}
                </div>
              ) : (
                <p className="text-gray-500">
                  Select an article to ask questions about it using our AI assistant.
                </p>
              )}
            </div>
          </div>
        </div>
      )}

      {trending.length > 0 && (
        <div className="mb-10">
          <h2 className="text-2xl font-bold text-orange-600 flex items-center mb-4">
            <FireIcon className="h-7 w-7 mr-2 text-orange-500" /> Trending Topics
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {trending.map((article, idx) => {
              const badgeColors = [
                'bg-yellow-400 text-yellow-900', // 1st
                'bg-gray-300 text-gray-800',     // 2nd
                'bg-orange-300 text-orange-900'  // 3rd
              ];
              return (
                <Link
                  to={`/article/${article._id}`}
                  key={article._id}
                  className={
                    `group bg-white rounded-lg shadow p-0 flex flex-col border-l-4 border-orange-400 hover:shadow-lg transition-shadow duration-200 focus:outline-none ` +
                    (idx < 3 ? badgeColors[idx] + ' border-2 border-orange-500' : '')
                  }
                  style={{ textDecoration: 'none' }}
                >
                  <div className="flex items-center gap-2 p-4 pb-0">
                    {idx < 3 && (
                      <span className={`inline-block px-2 py-0.5 rounded-full text-xs font-bold mr-2 ${badgeColors[idx]}`}>{`#${idx + 1}`}</span>
                    )}
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-primary-100 text-primary-800">
                      {article.category}
                    </span>
                    <span className="text-xs text-gray-400 ml-2">{new Date(article.publishedAt).toLocaleDateString()}</span>
                  </div>
                  {article.imageUrl && (
                    <img
                      src={article.imageUrl}
                      alt={article.title}
                      className="w-full h-40 object-cover rounded-t-lg mt-2 group-hover:scale-105 transition-transform duration-200"
                    />
                  )}
                  <div className="p-4 flex-1 flex flex-col">
                    <h3 className="text-lg font-semibold text-gray-900 mb-1 group-hover:text-orange-600">
                      {article.title}
                    </h3>
                    {article.summary && (
                      <p className="text-gray-600 text-sm mb-2 line-clamp-2">{article.summary}</p>
                    )}
                    {article.source?.name && (
                      <span className="text-xs text-gray-500 mb-2">By {article.source.name}</span>
                    )}
                    <div className="flex items-center gap-4 text-sm text-gray-500 mt-auto">
                      <span className="flex items-center gap-1"><HeartIcon className="h-4 w-4" /> {article.likeCount}</span>
                      <span className="flex items-center gap-1"><ChatBubbleOvalLeftEllipsisIcon className="h-4 w-4" /> {article.commentCount}</span>
                    </div>
                  </div>
                </Link>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}
