import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './HomeModern.css';

export default function ExternalArticle() {
  const { state } = useLocation();
  const article = state?.article;
  const navigate = useNavigate();

  if (!article) {
    return (
      <div className="modern-bg min-h-screen flex flex-col items-center justify-center py-12 px-4">
        <div className="bg-white rounded-2xl shadow-2xl p-10 border border-gray-100 text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Article not found</h2>
          <button onClick={() => navigate(-1)} className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-base font-semibold rounded-lg text-white bg-primary-600 hover:bg-primary-700">Go Back</button>
        </div>
      </div>
    );
  }

  return (
    <div className="modern-bg min-h-screen py-8">
      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-2xl shadow-2xl p-10 border border-gray-100">
          {article.urlToImage && (
            <img src={article.urlToImage} alt={article.title} className="w-full h-72 object-cover rounded-t-2xl mb-6" />
          )}
          <h1 className="text-3xl font-extrabold text-primary-700 mb-4 tracking-tight drop-shadow">{article.title}</h1>
          <div className="flex items-center gap-3 mb-4">
            <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-semibold bg-primary-100 text-primary-800">
              {article.source?.name}
            </span>
            <span className="text-xs text-gray-400">{new Date(article.publishedAt).toLocaleDateString()}</span>
          </div>
          <p className="text-gray-700 text-lg mb-6">{article.content || article.description}</p>
          <a href={article.url} target="_blank" rel="noopener noreferrer" className="inline-block mb-8 px-4 py-2 rounded-lg bg-blue-100 text-blue-700 font-semibold hover:bg-blue-200">Read Original</a>

          {/* --- AI Q&A Section (scaffold) --- */}
          <div className="mt-8 mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-2">AI Assistant</h2>
            <div className="bg-gray-50 rounded-lg p-4 mb-2">(AI Q&A, explanation, etc. goes here)</div>
          </div>

          {/* --- Translation Section (scaffold) --- */}
          <div className="mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-2">Translate</h2>
            <div className="bg-gray-50 rounded-lg p-4 mb-2">(Translation feature goes here)</div>
          </div>

          {/* --- Comments Section (scaffold) --- */}
          <div className="mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-2">Comments</h2>
            <div className="bg-gray-50 rounded-lg p-4 mb-2">(Comments for this article.url go here)</div>
          </div>

          {/* --- Likes/Bookmarks Section (scaffold) --- */}
          <div className="mb-2">
            <div className="bg-gray-50 rounded-lg p-4">(Like/Bookmark for this article.url go here)</div>
          </div>
        </div>
      </div>
    </div>
  );
}
