import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { HeartIcon, BookmarkIcon, ChatBubbleLeftEllipsisIcon, ArrowLeftIcon, GlobeAltIcon, CheckBadgeIcon } from '@heroicons/react/24/outline';
import './HomeModern.css';

export default function ExternalArticle() {
  const { state } = useLocation();
  const article = state?.article;
  const navigate = useNavigate();
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [aiLoading, setAiLoading] = useState(false);
  const [factCheckResult, setFactCheckResult] = useState('');
  const [factChecking, setFactChecking] = useState(false);
  const [translated, setTranslated] = useState('');
  const [translating, setTranslating] = useState(false);
  const [targetLang, setTargetLang] = useState('hi');
  const [liked, setLiked] = useState(false);
  const [bookmarked, setBookmarked] = useState(false);
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [commentLoading, setCommentLoading] = useState(false);
  const [refresh, setRefresh] = useState(false);

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!article) return;
    // Fetch like/bookmark status
    axios.get(`${process.env.REACT_APP_API_URL}/api/user/profile`, { headers: { 'x-auth-token': token } })
      .then(res => {
        setLiked(res.data.externalLikes?.includes(article.url));
        setBookmarked(res.data.externalBookmarks?.includes(article.url));
      });
    // Fetch comments
    axios.get(`${process.env.REACT_APP_API_URL}/api/news/external/comments`, {
      params: { url: article.url },
      headers: { 'x-auth-token': token }
    }).then(res => setComments(res.data));
  }, [article, refresh, token]);

  if (!article) {
    return (
      <div className="modern-bg min-h-screen flex flex-col items-center justify-center py-12 px-4">
        <div className="bg-white rounded-2xl shadow-2xl p-10 border border-gray-100 text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Article not found</h2>
          <button onClick={() => navigate(-1)} className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-base font-semibold rounded-lg text-white bg-primary-600 hover:bg-primary-700">Go Back</button>
        </div>
      </div>
    );
  }

  // --- Handlers ---
  const handleLike = async () => {
    try {
      if (liked) {
        await axios.delete(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { data: { url: article.url }, headers: { 'x-auth-token': token } });
        setLiked(false);
      } else {
        await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { url: article.url }, { headers: { 'x-auth-token': token } });
        setLiked(true);
      }
    } catch (err) { toast.error('Failed to update like'); }
  };
  const handleBookmark = async () => {
    try {
      if (bookmarked) {
        await axios.delete(`${process.env.REACT_APP_API_URL}/api/news/external/bookmark`, { data: { url: article.url }, headers: { 'x-auth-token': token } });
        setBookmarked(false);
      } else {
        await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/bookmark`, { url: article.url }, { headers: { 'x-auth-token': token } });
        setBookmarked(true);
      }
    } catch (err) { toast.error('Failed to update bookmark'); }
  };
  const handleAskAI = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;
    setAiLoading(true);
    setAnswer('');
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/api/ai/qa`, {
        question,
        context: article.content || article.description
      }, { headers: { 'x-auth-token': token } });
      setAnswer(res.data.answer || res.data.candidates?.[0]?.content?.parts?.[0]?.text || '');
    } catch (err) {
      toast.error('AI failed');
    } finally {
      setAiLoading(false);
    }
  };
  const handleFactCheck = async () => {
    setFactChecking(true);
    setFactCheckResult('');
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/api/ai/fact-check`, {
        content: article.content || article.description
      }, { headers: { 'x-auth-token': token } });
      setFactCheckResult(res.data.verdict);
    } catch (err) {
      toast.error('Fact-check failed');
    } finally {
      setFactChecking(false);
    }
  };
  const handleTranslate = async () => {
    setTranslating(true);
    setTranslated('');
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/api/ai/translate`, {
        text: article.content || article.description,
        targetLang
      }, { headers: { 'x-auth-token': token } });
      setTranslated(res.data.translation);
    } catch (err) {
      toast.error('Translation failed');
    } finally {
      setTranslating(false);
    }
  };
  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;
    setCommentLoading(true);
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/comment`, {
        url: article.url,
        content: commentText
      }, { headers: { 'x-auth-token': token } });
      setCommentText('');
      setRefresh(r => !r);
    } catch (err) {
      toast.error('Failed to add comment');
    } finally {
      setCommentLoading(false);
    }
  };

  return (
    <div className="modern-bg min-h-screen py-8">
      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-2xl shadow-2xl p-10 border border-gray-100">
          <button onClick={() => navigate(-1)} className="inline-flex items-center text-gray-600 hover:text-primary-700 mb-6 font-semibold"><ArrowLeftIcon className="h-5 w-5 mr-2" />Back</button>
          {article.urlToImage && (
            <img src={article.urlToImage} alt={article.title} className="w-full h-72 object-cover rounded-t-2xl mb-6" />
          )}
          <h1 className="text-3xl font-extrabold text-primary-700 mb-4 tracking-tight drop-shadow flex items-center gap-3">{article.title}
            <button onClick={handleLike} className={`ml-2 ${liked ? 'text-red-500' : 'text-gray-400'} hover:text-red-600`} title={liked ? 'Unlike' : 'Like'}><HeartIcon className="h-7 w-7" /></button>
            <button onClick={handleBookmark} className={`ml-2 ${bookmarked ? 'text-blue-600' : 'text-gray-400'} hover:text-blue-700`} title={bookmarked ? 'Unbookmark' : 'Bookmark'}><BookmarkIcon className="h-7 w-7" /></button>
          </h1>
          <div className="flex items-center gap-3 mb-4">
            <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-semibold bg-primary-100 text-primary-800">
              {article.source?.name}
            </span>
            <span className="text-xs text-gray-400">{new Date(article.publishedAt).toLocaleDateString()}</span>
          </div>
          <p className="text-gray-700 text-lg mb-6">{article.content || article.description}</p>
          <a href={article.url} target="_blank" rel="noopener noreferrer" className="inline-block mb-8 px-4 py-2 rounded-lg bg-blue-100 text-blue-700 font-semibold hover:bg-blue-200">Read Original</a>

          {/* --- AI Q&A Section --- */}
          <div className="mt-8 mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-2 flex items-center gap-2"><ChatBubbleLeftEllipsisIcon className="h-6 w-6" /> AI Assistant</h2>
            <form onSubmit={handleAskAI} className="flex flex-col gap-3 mb-2">
              <textarea className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500" rows={2} value={question} onChange={e => setQuestion(e.target.value)} placeholder="Ask a question about this article..." />
              <button type="submit" className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700 w-fit" disabled={aiLoading}>{aiLoading ? 'Asking...' : 'Ask AI'}</button>
            </form>
            {answer && <div className="bg-blue-50 rounded p-3 text-blue-900 mb-2">{answer}</div>}
            <button onClick={handleFactCheck} className="px-4 py-2 rounded-md bg-green-100 text-green-700 font-semibold hover:bg-green-200 flex items-center gap-2" disabled={factChecking}>{factChecking ? 'Fact-checking...' : <><CheckBadgeIcon className="h-5 w-5" /> Fact-Check</>}</button>
            {factCheckResult && <div className="mt-2 bg-green-50 rounded p-3 text-green-900">{factCheckResult}</div>}
          </div>

          {/* --- Translation Section --- */}
          <div className="mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-2 flex items-center gap-2"><GlobeAltIcon className="h-6 w-6" /> Translate</h2>
            <div className="flex gap-2 mb-2">
              <select value={targetLang} onChange={e => setTargetLang(e.target.value)} className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500">
                <option value="hi">Hindi</option>
                <option value="es">Spanish</option>
                <option value="fr">French</option>
                <option value="zh">Chinese</option>
                <option value="de">German</option>
                <option value="ar">Arabic</option>
              </select>
              <button onClick={handleTranslate} className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700" disabled={translating}>{translating ? 'Translating...' : 'Translate'}</button>
            </div>
            {translated && <div className="bg-yellow-50 rounded p-3 text-yellow-900 whitespace-pre-line">{translated}</div>}
          </div>

          {/* --- Comments Section --- */}
          <div className="mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-2 flex items-center gap-2"><ChatBubbleLeftEllipsisIcon className="h-6 w-6" /> Comments</h2>
            <form onSubmit={handleAddComment} className="flex gap-2 mb-4">
              <input type="text" value={commentText} onChange={e => setCommentText(e.target.value)} className="flex-1 rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500" placeholder="Add a comment..." disabled={commentLoading} />
              <button type="submit" className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700" disabled={commentLoading || !commentText.trim()}>{commentLoading ? 'Posting...' : 'Post'}</button>
            </form>
            <div className="space-y-4">
              {comments.length === 0 ? <div className="text-gray-500">No comments yet.</div> : comments.map((c, idx) => (
                <div key={idx} className="flex items-start gap-3 bg-gray-50 rounded-xl p-4 shadow">
                  {c.user?.profilePicture ? <img src={c.user.profilePicture} alt="profile" className="h-8 w-8 rounded-full object-cover" /> : <span className="h-8 w-8 rounded-full bg-gray-200 flex items-center justify-center text-gray-500 font-bold">{c.user?.name?.[0] || '?'}</span>}
                  <div>
                    <div className="flex items-center gap-2">
                      <span className="font-medium text-gray-800 text-sm">{c.user?.name || 'User'}</span>
                      <span className="text-xs text-gray-400">{new Date(c.createdAt).toLocaleString()}</span>
                    </div>
                    <p className="text-gray-700 text-sm mt-1">{c.content}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
