import React from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { HeartIcon, BookmarkIcon } from '@heroicons/react/24/outline';

const ArticleCard = ({ article, isLast, lastArticleRef }) => {
  const { t } = useTranslation();

  // Dummy handlers - in a real app, these would be passed as props or come from context
  const handleLike = () => console.log('Like clicked');
  const handleSave = () => console.log('Save clicked');

  return (
    <div
      ref={isLast ? lastArticleRef : null}
      className="bg-white dark:bg-slate-800 rounded-xl shadow-lg overflow-hidden hover:shadow-2xl hover:-translate-y-1 transition-all duration-300 border border-gray-100 dark:border-slate-700"
    >
      <div className="flex flex-col sm:flex-row">
        {(article.image_url || article.imageUrl || article.urlToImage) && (
          <div className="w-full sm:w-1/3 h-52 sm:h-auto">
            <img
              src={article.image_url || article.imageUrl || article.urlToImage}
              alt={article.title}
              className="w-full h-full object-cover"
            />
          </div>
        )}
        <div className="p-6 flex flex-col justify-between flex-1">
          <div>
            <span className="text-sm font-bold text-primary-600 dark:text-primary-400">
              {article.category || t('General')}
            </span>
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mt-2 mb-3 leading-snug">
              <Link
                to={article._id ? `/article/${article._id}` : '/external-article'}
                state={{ article }}
                className="hover:text-primary-600 dark:hover:text-primary-400 transition-colors duration-200"
              >
                {article.title}
              </Link>
            </h3>
            <p className="text-gray-600 dark:text-gray-300 line-clamp-3">
              {article.summary || article.description}
            </p>
          </div>
          <div className="mt-6 flex justify-between items-center">
            <div className="text-xs text-gray-500 dark:text-gray-400">
              <span className="font-semibold">
                {article.source?.name || t('Unknown Source')}
              </span>
              <span className="mx-2">&bull;</span>
              <span>
                {new Date(article.publishedAt).toLocaleDateString()}
              </span>
            </div>
            <div className="flex items-center gap-2">
              <button onClick={handleLike} className="text-gray-400 hover:text-red-500 transition-colors" aria-label="Like article">
                <HeartIcon className="w-5 h-5" />
              </button>
              <button onClick={handleSave} className="text-gray-400 hover:text-primary-500 transition-colors" aria-label="Save article">
                <BookmarkIcon className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ArticleCard;
