import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { BookmarkIcon, ChatBubbleLeftIcon, HeartIcon, ChatBubbleOvalLeftEllipsisIcon, FireIcon, XMarkIcon, ShareIcon, ArrowRightIcon } from '@heroicons/react/24/outline';
import { io } from 'socket.io-client';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useTranslation } from 'react-i18next';
import './HomeModern.css';

const TrendingCard = ({ article, onSelect }) => (
  <div className="trending-card h-48 cursor-pointer" onClick={() => onSelect(article)}>
    <img src={article.image_url || article.urlToImage || 'https://via.placeholder.com/400x300'} alt={article.title} />
    <div className="trending-card-overlay">
      <h3 className="font-bold text-sm leading-tight">{article.title}</h3>
    </div>
  </div>
);

const ArticleCard = ({ article, onSave, onSelect, isSaved }) => (
  <div className="article-card flex flex-col md:flex-row items-center gap-6 p-6">
    <img
      src={article.image_url || article.urlToImage || 'https://via.placeholder.com/400x300'}
      alt={article.title}
      className="w-full md:w-1/3 h-auto object-cover rounded-lg"
    />
    <div className="flex-1">
      <div className="flex items-center justify-between mb-2">
        <span className="text-xs font-semibold bg-blue-100 text-blue-800 px-2 py-0.5 rounded-full">{article.category || 'General'}</span>
        <button onClick={() => onSave(article)} title={isSaved ? "Saved" : "Save Article"}>
          <BookmarkIcon className={`h-6 w-6 ${isSaved ? 'text-blue-600 fill-current' : 'text-gray-400 hover:text-blue-600'}`} />
        </button>
      </div>
      <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-2 hover:text-blue-600 cursor-pointer" onClick={() => onSelect(article)}>
        {article.title}
      </h2>
      <p className="text-gray-600 dark:text-gray-300 text-sm mb-4">{article.description}</p>
      <div className="flex items-center justify-between text-xs text-gray-500 dark:text-gray-400">
        <span>{article.source?.name || 'News Source'}</span>
        <span>{new Date(article.publishedAt).toLocaleDateString()}</span>
      </div>
    </div>
  </div>
);

const AIPanel = ({ selectedArticle, onAsk }) => (
    <div className="ai-panel sticky top-24">
      <div className="flex items-center gap-3 mb-4">
        <ChatBubbleOvalLeftEllipsisIcon className="h-8 w-8" />
        <h2 className="text-2xl font-bold">AI Assistant</h2>
      </div>
      {selectedArticle ? (
        <div>
          <h3 className="font-semibold mb-2">Ask about:</h3>
          <p className="text-sm font-medium bg-white/20 p-2 rounded-md mb-4">{selectedArticle.title}</p>
          <form onSubmit={onAsk}>
            <textarea
              rows="4"
              className="w-full rounded-md border-gray-300 bg-white text-gray-900 placeholder-gray-500 focus:ring-2 focus:ring-white p-2"
              placeholder="Your question..."
            />
            <button type="submit" className="w-full mt-2 bg-white text-blue-600 font-bold py-2 px-4 rounded-md hover:bg-gray-200">
              Ask AI
            </button>
          </form>
        </div>
      ) : (
        <div className="text-center">
          <p className="font-medium">Select an article from your feed to start a conversation with the AI Assistant.</p>
        </div>
      )}
    </div>
  );

export default function Home() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  // Search state
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [searchSummary, setSearchSummary] = useState('');
  const [searching, setSearching] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [userId, setUserId] = useState(null);
  const [trending, setTrending] = useState([]);
  const [category, setCategory] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const { t } = useTranslation();
  const [externalLikes, setExternalLikes] = useState([]); // NewsAPI liked URLs
  const [userProfile, setUserProfile] = useState(null);
  const navigate = useNavigate();
  const [trendingPanelOpen, setTrendingPanelOpen] = useState(false);
  const [selectedTrending, setSelectedTrending] = useState(null);
  const [showAllTrending, setShowAllTrending] = useState(false);
  const [nextPage, setNextPage] = useState(null);
  const [loadingMore, setLoadingMore] = useState(false);
  const observer = useRef();
  const lastArticleRef = useCallback(node => {
    if (loadingMore) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new window.IntersectionObserver(entries => {
      if (entries[0].isIntersecting && nextPage) {
        loadMoreArticles();
      }
    });
    if (node) observer.current.observe(node);
  }, [loadingMore, nextPage]);

  useEffect(() => {
    fetchArticles();
    // Fetch external likes and user profile
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/profile`, {
          headers: { 'x-auth-token': token },
        });
        setExternalLikes(res.data.externalLikes || []);
        setUserProfile(res.data);
      } catch (err) {
        // ignore
      }
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const socket = io('http://localhost:5000'); // or your backend URL

    socket.on('new-article', (article) => {
      setArticles((prev) => [article, ...prev]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  useEffect(() => {
    const fetchNotifications = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/notifications`, {
        headers: { 'x-auth-token': token }
      });
      setNotifications(res.data);
    };
    fetchNotifications();
  }, []);

  useEffect(() => {
    // Get user ID from token (assumes JWT with sub or id field)
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUserId(payload.id || payload.sub || null);
      } catch (e) {
        setUserId(null);
      }
    }
  }, []);

  useEffect(() => {
    // Fetch trending articles
    const fetchTrending = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/trending`, {
          headers: { 'x-auth-token': token },
        });
        setTrending(res.data);
      } catch (err) {
        // Optionally toast error
      }
    };
    fetchTrending();
  }, []);

  const fetchArticles = async (reset = false) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setLoading(false);
        return;
      }
      setLoading(true);
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed`, {
        headers: { 'x-auth-token': token },
      });
      setArticles(res.data.articles || []);
      setNextPage(res.data.nextPage || null);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch articles');
      setLoading(false);
    }
  };

  const loadMoreArticles = async () => {
    if (!nextPage) return;
    setLoadingMore(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed?nextPage=${encodeURIComponent(nextPage)}`, {
        headers: { 'x-auth-token': token },
      });
      setArticles(prev => [...prev, ...(res.data.articles || [])]);
      setNextPage(res.data.nextPage || null);
    } catch (err) {
      toast.error('Failed to load more articles');
    } finally {
      setLoadingMore(false);
    }
  };

  const handleSaveArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/save`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        toast.success('Article saved successfully');
      } else {
        // NewsAPI/newsdata.io article
        const url = article.url || article.link;
        console.log('Sending bookmark for external article with url:', url);
        if (!url || typeof url !== 'string' || !url.startsWith('http')) {
          toast.error('No valid URL for this article.');
          return;
        }
        await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/bookmark`, { url }, { headers: { 'x-auth-token': token } });
        toast.success('Bookmarked!');
      }
    } catch (err) {
      toast.error('Failed to save article');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/qa`,
        {
          question,
          context: selectedArticle.content,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      setAnswer(res.data.answer);
    } catch (err) {
      toast.error('Failed to get answer');
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearching(true);
    try {
      const token = localStorage.getItem('token');
      const params = new URLSearchParams({ q: query });
      if (category) params.append('category', category);
      if (mediaType) params.append('mediaType', mediaType);
      if (startDate) params.append('startDate', startDate.toISOString());
      if (endDate) params.append('endDate', endDate.toISOString());
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/news/search?${params.toString()}`,
        { headers: { 'x-auth-token': token } }
      );
      setSearchResults(res.data.articles);
      setSearchSummary(res.data.summary);
    } catch (err) {
      toast.error('Failed to search');
    } finally {
      setSearching(false);
    }
  };

  const handleBackToFeed = () => {
    setSearchResults(null);
    setSearchSummary('');
    setQuery('');
  };

  const handleLikeArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        const res = await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/like`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        setArticles((prev) =>
          prev.map((a) =>
            a._id === article._id ? { ...a, likes: res.data.likes } : a
          )
        );
        if (searchResults) {
          setSearchResults((prev) =>
            prev.map((a) =>
              a._id === article._id ? { ...a, likes: res.data.likes } : a
            )
          );
        }
      } else {
        // NewsAPI/newsdata.io article
        const url = article.url || article.link;
        console.log('Sending like for external article with url:', url);
        if (!url || typeof url !== 'string' || !url.startsWith('http')) {
          toast.error('No valid URL for this article.');
          return;
        }
        if (externalLikes.includes(url)) {
          await axios.delete(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { data: { url }, headers: { 'x-auth-token': token } });
          setExternalLikes((prev) => prev.filter((u) => u !== url));
        } else {
          await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { url }, { headers: { 'x-auth-token': token } });
          setExternalLikes((prev) => [...prev, url]);
        }
      }
    } catch (err) {
      toast.error('Failed to like article');
    }
  };

  // Helper for greeting
  function getGreeting() {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 18) return 'Good afternoon';
    return 'Good evening';
  }

  // Helper for sharing (copy link)
  const handleShare = (url) => {
    if (navigator.clipboard && url) {
      navigator.clipboard.writeText(url);
      toast.success('Link copied!');
    }
  };

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
  }

  const handleAskAI = (e) => {
    e.preventDefault();
    toast.info("AI response functionality coming soon!");
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="modern-bg min-h-screen py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 pt-20">
        {/* Personalized Greeting and Profile */}
        {userProfile && (
          <div className="flex items-center gap-4 mb-8">
            <button onClick={() => navigate('/profile')} className="focus:outline-none">
              {userProfile.profilePicture ? (
                <img src={userProfile.profilePicture} alt="Profile" className="w-14 h-14 rounded-full object-cover border-4 border-primary-500 shadow-lg hover:scale-105 transition-transform" />
              ) : (
                <div className="w-14 h-14 rounded-full bg-gradient-to-br from-blue-400 to-purple-400 flex items-center justify-center text-2xl text-white font-bold shadow-lg">
                  {userProfile.name?.[0] || '?'}
                </div>
              )}
            </button>
            <div>
              <div className="text-lg font-semibold text-gray-700">{getGreeting()}, <span className="text-primary-700 font-bold">{userProfile.name}</span>!</div>
              {userProfile.bio && <div className="text-sm text-gray-500 mt-1">{userProfile.bio}</div>}
            </div>
          </div>
        )}
        {/* Main Feed Card */}
        <div className="bg-gradient-to-br from-blue-50 to-purple-50 dark:from-gray-800 dark:to-gray-900 rounded-2xl shadow-2xl p-10 mb-10 border border-blue-100 dark:border-gray-800">
          <h1 className="text-3xl font-extrabold text-primary-700 dark:text-primary-200 mb-8 tracking-tight drop-shadow">{t('Your Personalized News Feed')}</h1>
          {/* Search Bar */}
          <form onSubmit={handleSearch} className="mb-6 flex flex-col md:flex-row md:items-end gap-3 flex-wrap">
            <input
              value={query}
              onChange={e => setQuery(e.target.value)}
              placeholder={t('Search news...')}
              className="flex-1 min-w-[180px] rounded-md border border-gray-300 dark:border-gray-700 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 dark:bg-gray-800 dark:text-white"
            />
            <select
              value={category}
              onChange={e => setCategory(e.target.value)}
              className="min-w-[150px] rounded-md border border-gray-300 dark:border-gray-700 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 dark:bg-gray-800 dark:text-white"
            >
              <option value="">{t('All Categories')}</option>
              <option value="technology">{t('Technology')}</option>
              <option value="science">{t('Science')}</option>
              <option value="business">{t('Business')}</option>
              <option value="health">{t('Health')}</option>
              <option value="entertainment">{t('Entertainment')}</option>
              <option value="sports">{t('Sports')}</option>
              <option value="politics">{t('Politics')}</option>
              <option value="education">{t('Education')}</option>
            </select>
            <select
              value={mediaType}
              onChange={e => setMediaType(e.target.value)}
              className="min-w-[120px] rounded-md border border-gray-300 dark:border-gray-700 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 dark:bg-gray-800 dark:text-white"
            >
              <option value="">{t('All Media')}</option>
              <option value="text">{t('Text')}</option>
              <option value="image">{t('Image')}</option>
              <option value="video">{t('Video')}</option>
              <option value="podcast">{t('Podcast')}</option>
            </select>
            <DatePicker
              selected={startDate}
              onChange={date => setStartDate(date)}
              selectsStart
              startDate={startDate}
              endDate={endDate}
              placeholderText={t('Start Date')}
              className="rounded-md border border-gray-300 dark:border-gray-700 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 min-w-[120px] dark:bg-gray-800 dark:text-white"
              dateFormat="yyyy-MM-dd"
              isClearable
            />
            <DatePicker
              selected={endDate}
              onChange={date => setEndDate(date)}
              selectsEnd
              startDate={startDate}
              endDate={endDate}
              minDate={startDate}
              placeholderText={t('End Date')}
              className="rounded-md border border-gray-300 dark:border-gray-700 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 min-w-[120px] dark:bg-gray-800 dark:text-white"
              dateFormat="yyyy-MM-dd"
              isClearable
            />
            <button
              type="submit"
              className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800"
              disabled={searching}
            >
              {searching ? t('Searching...') : t('Search')}
            </button>
          </form>

          {/* Trending Horizontal Scroll Bar */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-bold text-primary-700 dark:text-primary-200 flex items-center"><FireIcon className="h-6 w-6 mr-2 text-orange-500" /> {t('Trending')}</h2>
              <button
                className="text-primary-600 dark:text-primary-300 font-semibold hover:underline text-sm px-3 py-1 rounded focus:outline-none"
                onClick={() => { setShowAllTrending(true); setTrendingPanelOpen(true); }}
              >
                {t('See All Trending')}
              </button>
            </div>
            {trending.length === 0 ? (
              <p className="text-gray-500 dark:text-gray-400">{t('No trending topics right now.')}</p>
            ) : (
              <div className="flex overflow-x-auto gap-4 pb-2 hide-scrollbar">
                {trending.map((article, idx) => (
                  <TrendingCard key={idx} article={article} onSelect={() => navigate('/external-article', { state: { article } })} />
                ))}
              </div>
            )}
          </div>

          {/* Trending Side Panel */}
          {trendingPanelOpen && (
            <div className="fixed inset-0 z-50 flex justify-end bg-black bg-opacity-30" onClick={() => setTrendingPanelOpen(false)}>
              <div
                className="w-full max-w-md h-full bg-white shadow-2xl p-6 overflow-y-auto relative animate-slide-in-right"
                onClick={e => e.stopPropagation()}
                tabIndex={-1}
                aria-modal="true"
                role="dialog"
              >
                <button
                  className="absolute top-4 right-4 text-gray-400 hover:text-primary-600 focus:outline-none"
