import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { BookmarkIcon, ChatBubbleLeftIcon, ArrowLeftIcon, MicrophoneIcon, SpeakerWaveIcon, XMarkIcon, UserIcon, GlobeAltIcon } from '@heroicons/react/24/outline';
import { useTranslation } from 'react-i18next';

export default function Article() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [complexity, setComplexity] = useState('intermediate');
  const [explanation, setExplanation] = useState('');
  const [commentText, setCommentText] = useState('');
  const [submittingComment, setSubmittingComment] = useState(false);
  const [translatedContent, setTranslatedContent] = useState('');
  const [translating, setTranslating] = useState(false);
  const [targetLang, setTargetLang] = useState('hi');
  const { t } = useTranslation();

  useEffect(() => {
    fetchArticle();
  }, [id]);

  const fetchArticle = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/article/${id}`, {
        headers: {
          'x-auth-token': token,
        },
      });
      setArticle(res.data);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch article');
      setLoading(false);
    }
  };

  const handleSaveArticle = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/news/article/${id}/save`,
        {},
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      toast.success('Article saved successfully');
    } catch (err) {
      toast.error('Failed to save article');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/qa`,
        {
          question,
          context: article.content,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      console.log('AI ask response:', res.data);
      setAnswer(
        res.data.answer ||
        res.data.candidates?.[0]?.content?.parts?.[0]?.text ||
        ''
      );
    } catch (err) {
      toast.error(
        err.response?.data?.msg ||
        err.response?.data?.error ||
        'Failed to get answer'
      );
    }
  };

  const handleExplain = async () => {
    if (!article?.title || !complexity) {
      toast.error('Please select an article and explanation level.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/explain`,
        {
          question: `${article.title} (${complexity})`,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      console.log('AI explain response:', res.data);
      // Try direct explanation, then Gemini nested structure
      setExplanation(
        res.data.explanation ||
        res.data.candidates?.[0]?.content?.parts?.[0]?.text ||
        ''
      );
    } catch (err) {
      toast.error(
        err.response?.data?.msg ||
        err.response?.data?.error ||
        'Failed to get explanation'
      );
    }
  };

  const handleVoiceInput = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech recognition not supported in this browser.');
      return;
    }
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
    };
    recognition.start();
  };

  const handleSpeak = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new window.SpeechSynthesisUtterance(text);
      window.speechSynthesis.speak(utterance);
    } else {
      alert('Text-to-speech not supported in this browser.');
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;
    setSubmittingComment(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/news/article/${id}/comment`,
        { content: commentText },
        { headers: { 'x-auth-token': token } }
      );
      setArticle((prev) => ({ ...prev, comments: res.data }));
      setCommentText('');
    } catch (err) {
      toast.error('Failed to add comment');
    } finally {
      setSubmittingComment(false);
    }
  };

  const handleTranslate = async () => {
    if (!article?.content) return;
    setTranslating(true);
    setTranslatedContent('');
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/translate`,
        { text: article.content, targetLang },
        { headers: { 'x-auth-token': token } }
      );
      setTranslatedContent(res.data.translation);
    } catch (err) {
      toast.error(
        err.response?.data?.msg || err.response?.data?.error || 'Translation failed'
      );
    } finally {
      setTranslating(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!article) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900">{t('Article not found')}</h2>
        <button
          onClick={() => navigate('/')}
          className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
        >
          <ArrowLeftIcon className="h-5 w-5 mr-2" />
          {t('Back to Home')}
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
      <button
        onClick={() => navigate('/')}
        className="inline-flex items-center text-gray-600 hover:text-gray-900 mb-6"
      >
        <ArrowLeftIcon className="h-5 w-5 mr-2" />
        {t('Back to Feed')}
      </button>

      <article className="bg-white rounded-lg shadow-md overflow-hidden">
        {article.imageUrl && (
          <img
            src={article.imageUrl}
            alt={article.title}
            className="w-full h-64 object-cover"
          />
        )}
        <div className="p-6">
          <div className="flex items-center justify-between mb-4">
            <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-medium bg-primary-100 text-primary-800">
              {article.category}
            </span>
            <button
              onClick={handleSaveArticle}
              className="text-gray-400 hover:text-primary-600"
            >
              <BookmarkIcon className="h-6 w-6" />
            </button>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-4">{article.title}</h1>
          <div className="prose max-w-none mb-8">
            <p className="text-gray-600">{article.content}</p>
            <div className="flex items-center gap-2 mt-4">
              <select
                value={targetLang}
                onChange={e => setTargetLang(e.target.value)}
                className="rounded-md border-gray-300 px-2 py-1 text-sm"
              >
                <option value="hi">{t('Hindi')}</option>
                <option value="en">{t('English')}</option>
                <option value="es">{t('Spanish')}</option>
                <option value="fr">{t('French')}</option>
                <option value="zh">{t('Chinese')}</option>
                <option value="de">{t('German')}</option>
                <option value="ar">{t('Arabic')}</option>
              </select>
              <button
                onClick={handleTranslate}
                disabled={translating}
                className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
              >
                <GlobeAltIcon className="h-5 w-5 mr-1" />
                {translating ? t('Translating...') : t('Translate')}
              </button>
            </div>
            {translatedContent && (
              <div className="mt-4 p-4 bg-yellow-50 rounded-md">
                <h4 className="font-medium text-yellow-900 mb-2">{t('Translated Content')}:</h4>
                <p className="text-gray-800 whitespace-pre-line">{translatedContent}</p>
              </div>
            )}
          </div>
          <div className="border-t border-gray-200 pt-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">{t('AI Assistant')}</h2>
            <div className="space-y-6">
              {/* Question Form */}
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">{t('Ask a Question')}</h3>
                <form onSubmit={handleAskQuestion} className="space-y-4">
                  <div>
                    <label htmlFor="question" className="block text-sm font-medium text-gray-700">
                      {t('Your question')}
                    </label>
                    <textarea
                      id="question"
                      rows={3}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      placeholder={t('What would you like to know about this article?')}
                    />
                  </div>
                  <button
                    type="button"
                    onClick={handleVoiceInput}
                    className="ml-2 p-2 rounded-full bg-gray-200 hover:bg-primary-100"
                    title={t('Speak your question')}
                  >
                    <MicrophoneIcon className="h-5 w-5 text-gray-600" />
                  </button>
                  <button
                    type="submit"
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
                  >
                    <ChatBubbleLeftIcon className="h-5 w-5 mr-2" />
                    {t('Ask AI')}
                  </button>
                </form>
                {answer && (
                  <div className="mt-4 p-4 bg-gray-50 rounded-md">
                    <h4 className="font-medium text-gray-900 mb-2">{t('AI Response')}:</h4>
                    <p className="text-gray-600">{answer}</p>
                    <button
                      type="button"
                      onClick={() => handleSpeak(answer)}
                      className="ml-2 p-2 rounded-full bg-gray-200 hover:bg-primary-100"
                      title={t('Listen to answer')}
                    >
                      <SpeakerWaveIcon className="h-5 w-5 text-gray-600" />
                    </button>
                    <button
                      type="button"
                      onClick={() => window.speechSynthesis.cancel()}
                      className="ml-2 p-2 rounded-full bg-gray-200 hover:bg-red-100"
                      title={t('Stop speaking')}
                    >
                      <XMarkIcon className="h-5 w-5 text-red-600" />
                    </button>
                  </div>
                )}
              </div>

              {/* Explanation Section */}
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">{t('Get an Explanation')}</h3>
                <div className="space-y-4">
                  <div>
                    <label htmlFor="complexity" className="block text-sm font-medium text-gray-700">
                      {t('Explanation Level')}
                    </label>
                    <select
                      id="complexity"
                      value={complexity}
                      onChange={(e) => setComplexity(e.target.value)}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                    >
                      <option value="beginner">{t('Beginner')}</option>
                      <option value="intermediate">{t('Intermediate')}</option>
                      <option value="advanced">{t('Advanced')}</option>
                    </select>
                  </div>
                  <button
                    onClick={handleExplain}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
                  >
                    {t('Explain Article')}
                  </button>
                  {explanation && (
                    <div className="mt-4 p-4 bg-gray-50 rounded-md">
                      <h4 className="font-medium text-gray-900 mb-2">{t('AI Explanation')}:</h4>
                      <p className="text-gray-600">{explanation}</p>
                      <button
                        type="button"
                        onClick={() => handleSpeak(explanation)}
                        className="ml-2 p-2 rounded-full bg-gray-200 hover:bg-primary-100"
                        title={t('Listen to explanation')}
                      >
                        <SpeakerWaveIcon className="h-5 w-5 text-gray-600" />
                      </button>
                      <button
                        type="button"
                        onClick={() => window.speechSynthesis.cancel()}
                        className="ml-2 p-2 rounded-full bg-gray-200 hover:bg-red-100"
                        title={t('Stop speaking')}
                      >
                        <XMarkIcon className="h-5 w-5 text-red-600" />
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
          <div className="border-t border-gray-200 pt-6 mt-8">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">{t('Comments')}</h2>
            <form onSubmit={handleAddComment} className="mb-6 flex gap-2">
              <input
                type="text"
                value={commentText}
                onChange={e => setCommentText(e.target.value)}
                placeholder={t('Add a comment...')}
                className="flex-1 rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
                disabled={submittingComment}
              />
              <button
                type="submit"
                className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700"
                disabled={submittingComment || !commentText.trim()}
              >
                {submittingComment ? t('Posting...') : t('Post')}
              </button>
            </form>
            <div className="space-y-4">
              {article.comments && article.comments.length > 0 ? (
                article.comments.slice().reverse().map((comment, idx) => (
                  <div key={comment._id || idx} className="flex items-start gap-3 bg-gray-50 rounded-md p-3">
                    <UserIcon className="h-6 w-6 text-gray-400 mt-1" />
                    <div>
                      <div className="flex items-center gap-2">
                        <span className="font-medium text-gray-800 text-sm">{comment.user?.name || t('User')}</span>
                        <span className="text-xs text-gray-400">{new Date(comment.createdAt).toLocaleString()}</span>
                      </div>
                      <p className="text-gray-700 text-sm mt-1">{comment.content}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-gray-500">{t('No comments yet. Be the first to comment!')}</p>
              )}
            </div>
          </div>
        </div>
      </article>
    </div>
  );
}
