import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { BookmarkIcon, ChatBubbleLeftIcon, HeartIcon, ChatBubbleOvalLeftEllipsisIcon, FireIcon, XMarkIcon, ShareIcon } from '@heroicons/react/24/outline';
import { io } from 'socket.io-client';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useTranslation } from 'react-i18next';
import './HomeModern.css';

export default function Home() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  // Search state
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [searchSummary, setSearchSummary] = useState('');
  const [searching, setSearching] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [userId, setUserId] = useState(null);
  const [trending, setTrending] = useState([]);
  const [category, setCategory] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const { t } = useTranslation();
  const [externalLikes, setExternalLikes] = useState([]); // NewsAPI liked URLs
  const [userProfile, setUserProfile] = useState(null);
  const navigate = useNavigate();
  const [trendingPanelOpen, setTrendingPanelOpen] = useState(false);
  const [selectedTrending, setSelectedTrending] = useState(null);
  const [showAllTrending, setShowAllTrending] = useState(false);
  const [nextPage, setNextPage] = useState(null);
  const [loadingMore, setLoadingMore] = useState(false);
  const observer = useRef();
  const lastArticleRef = useCallback(node => {
    if (loadingMore) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new window.IntersectionObserver(entries => {
      if (entries[0].isIntersecting && nextPage) {
        loadMoreArticles();
      }
    });
    if (node) observer.current.observe(node);
  }, [loadingMore, nextPage]);

  useEffect(() => {
    fetchArticles();
    // Fetch external likes and user profile
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/profile`, {
          headers: { 'x-auth-token': token },
        });
        setExternalLikes(res.data.externalLikes || []);
        setUserProfile(res.data);
      } catch (err) {
        // ignore
      }
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const socket = io('http://localhost:5000'); // or your backend URL

    socket.on('new-article', (article) => {
      setArticles((prev) => [article, ...prev]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  useEffect(() => {
    const fetchNotifications = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/notifications`, {
        headers: { 'x-auth-token': token }
      });
      setNotifications(res.data);
    };
    fetchNotifications();
  }, []);

  useEffect(() => {
    // Get user ID from token (assumes JWT with sub or id field)
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUserId(payload.id || payload.sub || null);
      } catch (e) {
        setUserId(null);
      }
    }
  }, []);

  useEffect(() => {
    // Fetch trending articles
    const fetchTrending = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/trending`, {
          headers: { 'x-auth-token': token },
        });
        setTrending(res.data);
      } catch (err) {
        // Optionally toast error
      }
    };
    fetchTrending();
  }, []);

  const fetchArticles = async (reset = false) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setLoading(false);
        return;
      }
      setLoading(true);
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed`, {
        headers: { 'x-auth-token': token },
      });
      setArticles(res.data.articles || []);
      setNextPage(res.data.nextPage || null);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch articles');
      setLoading(false);
    }
  };

  const loadMoreArticles = async () => {
    if (!nextPage) return;
    setLoadingMore(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed?nextPage=${encodeURIComponent(nextPage)}`, {
        headers: { 'x-auth-token': token },
      });
      setArticles(prev => [...prev, ...(res.data.articles || [])]);
      setNextPage(res.data.nextPage || null);
    } catch (err) {
      toast.error('Failed to load more articles');
    } finally {
      setLoadingMore(false);
    }
  };

  const handleSaveArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/save`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        toast.success('Article saved successfully');
      } else {
        // NewsAPI/newsdata.io article
        const url = article.url || article.link;
        console.log('Sending bookmark for external article with url:', url);
        if (!url || typeof url !== 'string' || !url.startsWith('http')) {
          toast.error('No valid URL for this article.');
          return;
        }
        await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/bookmark`, { url }, { headers: { 'x-auth-token': token } });
        toast.success('Bookmarked!');
      }
    } catch (err) {
      toast.error('Failed to save article');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/qa`,
        {
          question,
          context: selectedArticle.content,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      setAnswer(res.data.answer);
    } catch (err) {
      toast.error('Failed to get answer');
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearching(true);
    try {
      const token = localStorage.getItem('token');
      const params = new URLSearchParams({ q: query });
      if (category) params.append('category', category);
      if (mediaType) params.append('mediaType', mediaType);
      if (startDate) params.append('startDate', startDate.toISOString());
      if (endDate) params.append('endDate', endDate.toISOString());
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/news/search?${params.toString()}`,
        { headers: { 'x-auth-token': token } }
      );
      setSearchResults(res.data.articles);
      setSearchSummary(res.data.summary);
    } catch (err) {
      toast.error('Failed to search');
    } finally {
      setSearching(false);
    }
  };

  const handleBackToFeed = () => {
    setSearchResults(null);
    setSearchSummary('');
    setQuery('');
  };

  const handleLikeArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        const res = await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/like`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        setArticles((prev) =>
          prev.map((a) =>
            a._id === article._id ? { ...a, likes: res.data.likes } : a
          )
        );
      } else {
        // External article
        const url = article.url || article.link;
        if (!url || typeof url !== 'string' || !url.startsWith('http')) return;
        if (externalLikes.includes(url)) {
          // Unlike
          await axios.delete(`${process.env.REACT_APP_API_URL}/api/news/external/like`, {
            data: { url },
            headers: { 'x-auth-token': token }
          });
          setExternalLikes(prev => prev.filter(l => l !== url));
        } else {
          // Like
          await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { url }, { headers: { 'x-auth-token': token } });
          setExternalLikes(prev => [...prev, url]);
        }
      }
    } catch (err) {
      toast.error('Failed to update like');
    }
  };

  function getGreeting() {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 18) return 'Good afternoon';
    return 'Good evening';
  }

  const handleShare = (url) => {
    navigator.clipboard.writeText(url)
      .then(() => toast.success('Link copied to clipboard!'))
      .catch(() => toast.error('Failed to copy link.'));
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="modern-bg min-h-screen">
      <div className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <header className="mb-8">
          <h1 className="text-4xl font-extrabold text-primary-700 dark:text-primary-200 tracking-tight drop-shadow-md">
            {getGreeting()}, {userProfile?.name?.split(' ')[0] || 'Guest'}
          </h1>
          <p className="text-lg text-gray-600 dark:text-gray-300 mt-1">Here's your personalized briefing for today.</p>
        </header>

        {/* Search and Filter Section */}
        <div className="blended-card p-6 mb-8 border-2 border-transparent bg-clip-padding backdrop-blur-md shadow-xl">
          <form onSubmit={handleSearch} className="flex flex-col md:flex-row gap-4 items-center">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={t('Search news...')}
              className="flex-grow rounded-lg border border-gray-300 dark:border-gray-700 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm bg-white/80 dark:bg-gray-800/80 text-gray-900 dark:text-white"
            />
            <div className="flex gap-2">
              <DatePicker selected={startDate} onChange={date => setStartDate(date)} placeholderText="Start Date" className="w-full rounded-lg border-gray-300 dark:border-gray-700 shadow-sm bg-white/80 dark:bg-gray-800/80 text-gray-900 dark:text-white" />
              <DatePicker selected={endDate} onChange={date => setEndDate(date)} placeholderText="End Date" className="w-full rounded-lg border-gray-300 dark:border-gray-700 shadow-sm bg-white/80 dark:bg-gray-800/80 text-gray-900 dark:text-white" />
            </div>
            <button type="submit" disabled={searching} className="px-5 py-2 rounded-lg shadow font-semibold text-white bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 dark:from-blue-800 dark:to-purple-800 dark:hover:from-blue-900 dark:hover:to-purple-900 transition-all">
              {searching ? 'Searching...' : 'Search'}
            </button>
            {searchResults && (
              <button type="button" onClick={handleBackToFeed} className="px-5 py-2 rounded-lg font-semibold text-gray-700 dark:text-gray-200 bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600">Back</button>
            )}
          </form>
        </div>

        {searchResults ? (
          <div className="blended-card p-6">
            <h2 className="text-2xl font-bold text-primary-700 dark:text-primary-200 mb-4">Search Results</h2>
            {searchSummary && (
              <div className="mb-4 p-4 bg-blue-50 dark:bg-blue-900/30 rounded-lg">
                <h3 className="font-semibold text-blue-900 dark:text-blue-200">AI Summary:</h3>
                <p className="text-blue-800 dark:text-blue-100 whitespace-pre-line">{searchSummary}</p>
              </div>
            )}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {searchResults.map((article, idx) => (
                <div key={idx} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-4 flex flex-col justify-between">
                  {/* ... simplified card for search results ... */}
                  <h3 className="font-bold text-gray-900 dark:text-white">{article.title}</h3>
                  <p className="text-sm text-gray-600 dark:text-gray-300 line-clamp-3">{article.description}</p>
                  <a href={article.url || article.link} target="_blank" rel="noopener noreferrer" className="text-primary-600 dark:text-primary-300 hover:underline mt-2 self-start">Read More</a>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <>
            {/* Trending Section */}
            <section className="mb-12">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold text-primary-700 dark:text-primary-200 flex items-center gap-2">
                  <FireIcon className="h-6 w-6 text-red-500" /> {t('Trending')}
                </h2>
                <button
                  className="text-sm font-semibold text-primary-600 dark:text-primary-300 hover:underline"
                  onClick={() => { setTrendingPanelOpen(true); setShowAllTrending(true); }}
                >
                  See All Trending
                </button>
              </div>
              <div className="relative">
                <div className="flex overflow-x-auto gap-4 pb-2 hide-scrollbar">
                  {trending.map((article, idx) => (
                    <div
                      key={article._id || article.url || article.link || idx}
                      className="min-w-[320px] max-w-xs bg-gradient-to-br from-blue-50 to-purple-50 dark:from-gray-800 dark:to-gray-900 rounded-xl shadow-md border border-blue-100 dark:border-gray-800 flex-shrink-0 hover:shadow-lg transition-shadow duration-200 cursor-pointer"
                      tabIndex={0}
                      aria-label={article.title}
                      onClick={() => { setSelectedTrending(article); setTrendingPanelOpen(true); setShowAllTrending(false); }}
                      onKeyDown={e => { if (e.key === 'Enter') { setSelectedTrending(article); setTrendingPanelOpen(true); setShowAllTrending(false); }}}
                    >
                      {(() => { const imgSrc = article.image_url || article.imageUrl || article.urlToImage; return imgSrc ? (
                        <img src={imgSrc} alt={article.title} className="w-full h-32 object-cover rounded-t-xl" />
                      ) : (
                        <div className="w-full h-32 flex items-center justify-center text-4xl text-gray-300"></div>
                      ); })()}
                      <div className="p-4 flex flex-col gap-2">
                        <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-semibold bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow">
                          {article.category || article.source?.name || 'Trending'}
                        </span>
                        <h3 className="text-base font-bold text-gray-900 dark:text-white line-clamp-2 mb-1">{article.title}</h3>
                        <span className="text-xs text-gray-400 dark:text-gray-300">{article.publishedAt ? new Date(article.publishedAt).toLocaleDateString() : ''}</span>
                        {article.source?.name && <span className="text-xs text-blue-700 dark:text-blue-300 font-semibold">{article.source.name}</span>}
                        <a href={article.url || article.link} target="_blank" rel="noopener noreferrer" className="mt-2 text-xs text-primary-600 dark:text-primary-300 hover:underline font-semibold">Read</a>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </section>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              {/* Main Content Feed */}
              <div className="lg:col-span-2">
                <h2 className="text-2xl font-bold text-primary-700 dark:text-primary-200 mb-4">{t('Your Personalized Feed')}</h2>
                <div className="space-y-6">
                  {articles.map((article, idx) => (
                    <div
                      ref={idx === articles.length - 1 ? lastArticleRef : null}
                      key={article._id || article.url || article.link || idx}
                      className="blended-card p-6 flex flex-col md:flex-row gap-6 items-start border-2 border-transparent bg-clip-padding backdrop-blur-md shadow-xl hover:shadow-2xl transition-all duration-300"
                    >
                      {(() => { const imgSrc = article.image_url || article.imageUrl || article.urlToImage; return imgSrc ? (
                        <img src={imgSrc} alt={article.title} className="w-full md:w-1/4 h-auto object-cover rounded-lg aspect-square" />
                      ) : (
                        <div className="w-full md:w-1/4 h-auto flex items-center justify-center text-6xl text-gray-300"></div>
                      ); })()}
                      <div className="flex-1">
                        <div className="flex justify-between items-start mb-2">
                          <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-semibold bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow">
                            {article.category || article.source?.name || 'General'}
                          </span>
                          <div className="flex items-center gap-2">
                            <button onClick={() => handleLikeArticle(article)} title="Like">
                              <HeartIcon className={`h-6 w-6 ${externalLikes.includes(article.url || article.link) || article.likes?.includes(userId) ? 'text-red-500' : 'text-gray-400'} hover:text-red-500`} />
                            </button>
                            <button onClick={() => handleSaveArticle(article)} title="Save Article">
                              <BookmarkIcon className="h-6 w-6 text-gray-400 hover:text-blue-600" />
                            </button>
                            <button onClick={() => handleShare(article.url || article.link)} title="Share">
                              <ShareIcon className="h-6 w-6 text-gray-400 hover:text-green-600" />
                            </button>
                          </div>
                        </div>
                        <h3
                          className="text-xl font-bold text-gray-900 dark:text-white mb-2 hover:text-primary-600 cursor-pointer"
                          onClick={() => {
                            if (article._id) {
                              navigate(`/article/${article._id}`);
                            } else {
                              navigate('/external-article', { state: { article } });
                            }
                          }}
                        >
                          {article.title}
                        </h3>
                        <p className="text-gray-600 dark:text-gray-300 text-sm mb-3 line-clamp-3">{article.description || article.summary}</p>
                        <div className="flex justify-between items-center text-xs text-gray-500 dark:text-gray-400">
                          <span>By {article.creator || article.source?.name || 'Unknown'}</span>
                          <span>{article.publishedAt ? new Date(article.publishedAt).toLocaleDateString() : ''}</span>
                        </div>
                      </div>
                    </div>
                  ))}
                  {loadingMore && <div className="text-center">Loading more articles...</div>}
                </div>
              </div>

              {/* AI Assistant Panel */}
              <div className="lg:col-span-1">
                <div className="blended-card p-6 sticky top-6 border-2 border-transparent bg-clip-padding backdrop-blur-md shadow-xl transition-all duration-300" style={{ borderImage: 'linear-gradient(135deg, #6366f1 0%, #a21caf 100%) 1' }}>
                  <div className="flex items-center gap-2 mb-4">
                    <svg className="h-6 w-6 text-primary-600 dark:text-primary-300" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M12 20h9" /><path d="M12 4v16m0 0l-4-4m4 4l4-4" /></svg>
                    <h3 className="text-lg font-bold text-primary-700 dark:text-primary-200 tracking-tight">AI Assistant</h3>
                  </div>
                  {selectedArticle ? (
                    <div>
                      <h4 className="font-semibold text-gray-900 dark:text-white mb-2 truncate">{selectedArticle.title}</h4>
                      <form onSubmit={handleAskQuestion} className="space-y-4">
                        <div>
                          <label htmlFor="question" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                            Ask anything about this article
                          </label>
                          <textarea
                            id="question"
                            rows={3}
                            className="mt-1 block w-full rounded-lg border border-gray-300 dark:border-gray-700 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm bg-white/80 dark:bg-gray-800/80 text-gray-900 dark:text-white resize-none"
                            value={question}
                            onChange={(e) => setQuestion(e.target.value)}
                            placeholder="E.g. Summarize, explain, fact-check, or ask for insights..."
                          />
                        </div>
                        <button
                          type="submit"
                          className="w-full flex justify-center py-2 px-4 rounded-lg shadow font-semibold text-white bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 dark:from-blue-800 dark:to-purple-800 dark:hover:from-blue-900 dark:hover:to-purple-900 transition-all"
                        >
                          Ask AI
                        </button>
                      </form>
                      {answer && (
                        <div className="mt-4 p-4 bg-gray-50 dark:bg-gray-800/80 rounded-lg shadow-inner">
                          <h5 className="font-medium text-primary-700 dark:text-primary-200 mb-2">AI Response:</h5>
                          <p className="text-gray-700 dark:text-gray-200 whitespace-pre-line">{answer}</p>
                        </div>
                      )}
                    </div>
                  ) : (
                    <div className="text-gray-500 dark:text-gray-300 text-sm flex flex-col items-center gap-2 py-8">
                      <svg className="h-10 w-10 text-primary-200 dark:text-primary-800 mb-2" fill="none" stroke="currentColor" strokeWidth="1.5" viewBox="0 0 24 24"><path d="M12 20h9" /><path d="M12 4v16m0 0l-4-4m4 4l4-4" /></svg>
                      <span>Select an article to ask questions, get summaries, or insights using our AI assistant.</span>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </>
        )}
      </div>

      {/* Side Panel for Trending Article Detail */}
      {trendingPanelOpen && selectedTrending && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm z-40" onClick={() => setTrendingPanelOpen(false)}>
          <div
            className="fixed top-0 right-0 h-full w-full max-w-md bg-white dark:bg-gray-900 shadow-2xl z-50 p-6 overflow-y-auto transform transition-transform duration-300 ease-in-out"
            onClick={(e) => e.stopPropagation()}
            style={{ transform: trendingPanelOpen ? 'translateX(0)' : 'translateX(100%)' }}
          >
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold text-primary-700 dark:text-primary-200">Trending Article</h2>
              <button onClick={() => setTrendingPanelOpen(false)} className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700">
                <XMarkIcon className="h-6 w-6" />
              </button>
            </div>
            {showAllTrending ? (
              <div className="space-y-4">
                <h3 className="text-lg font-bold">All Trending Articles</h3>
                {trending.map((article, idx) => (
                  <div key={idx} className="border p-2 rounded-lg">
                    <p>{article.title}</p>
                    <a href={article.url || article.link} target="_blank" rel="noopener noreferrer" className="text-blue-500">Read More</a>
                  </div>
                ))}
              </div>
            ) : (
              <div>
                <img src={selectedTrending.image_url || selectedTrending.urlToImage} alt={selectedTrending.title} className="w-full h-48 object-cover rounded-lg mb-4"/>
                <h3 className="text-lg font-bold mb-2">{selectedTrending.title}</h3>
                <p className="text-sm text-gray-600 dark:text-gray-300 mb-4">{selectedTrending.description}</p>
                <a href={selectedTrending.url || selectedTrending.link} target="_blank" rel="noopener noreferrer" className="font-semibold text-blue-600 hover:underline">Read Full Article</a>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
