import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { BookmarkIcon, ChatBubbleLeftIcon, HeartIcon, ChatBubbleOvalLeftEllipsisIcon, FireIcon } from '@heroicons/react/24/outline';
import { io } from 'socket.io-client';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useTranslation } from 'react-i18next';
import './HomeModern.css';

export default function Home() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  // Search state
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [searchSummary, setSearchSummary] = useState('');
  const [searching, setSearching] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [userId, setUserId] = useState(null);
  const [trending, setTrending] = useState([]);
  const [category, setCategory] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const { t } = useTranslation();
  const [externalLikes, setExternalLikes] = useState([]); // NewsAPI liked URLs

  useEffect(() => {
    fetchArticles();
    // Fetch external likes
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/profile`, {
          headers: { 'x-auth-token': token },
        });
        setExternalLikes(res.data.externalLikes || []);
      } catch (err) {
        // ignore
      }
    };
    fetchProfile();
  }, []);
  
  useEffect(() => {
    const socket = io('http://localhost:5000'); // or your backend URL

    socket.on('new-article', (article) => {
      setArticles((prev) => [article, ...prev]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  useEffect(() => {
    const fetchNotifications = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/notifications`, {
        headers: { 'x-auth-token': token }
      });
      setNotifications(res.data);
    };
    fetchNotifications();
  }, []);

  useEffect(() => {
    // Get user ID from token (assumes JWT with sub or id field)
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUserId(payload.id || payload.sub || null);
      } catch (e) {
        setUserId(null);
      }
    }
  }, []);

  useEffect(() => {
    // Fetch trending articles
    const fetchTrending = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/trending`, {
          headers: { 'x-auth-token': token },
        });
        setTrending(res.data);
      } catch (err) {
        // Optionally toast error
      }
    };
    fetchTrending();
  }, []);

  const fetchArticles = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setLoading(false);
        return;
      }

      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed`, {
        headers: {
          'x-auth-token': token,
        },
      });
      setArticles(res.data);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch articles');
      setLoading(false);
    }
  };

  const handleSaveArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/save`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        toast.success('Article saved successfully');
      } else {
        // NewsAPI article
        await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/bookmark`, { url: article.url }, { headers: { 'x-auth-token': token } });
        toast.success('Bookmarked!');
      }
    } catch (err) {
      toast.error('Failed to save article');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/qa`,
        {
          question,
          context: selectedArticle.content,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      setAnswer(res.data.answer);
    } catch (err) {
      toast.error('Failed to get answer');
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearching(true);
    try {
      const token = localStorage.getItem('token');
      const params = new URLSearchParams({ q: query });
      if (category) params.append('category', category);
      if (mediaType) params.append('mediaType', mediaType);
      if (startDate) params.append('startDate', startDate.toISOString());
      if (endDate) params.append('endDate', endDate.toISOString());
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/news/search?${params.toString()}`,
        { headers: { 'x-auth-token': token } }
      );
      setSearchResults(res.data.articles);
      setSearchSummary(res.data.summary);
    } catch (err) {
      toast.error('Failed to search');
    } finally {
      setSearching(false);
    }
  };

  const handleBackToFeed = () => {
    setSearchResults(null);
    setSearchSummary('');
    setQuery('');
  };

  const handleLikeArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        const res = await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/like`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        setArticles((prev) =>
          prev.map((a) =>
            a._id === article._id ? { ...a, likes: res.data.likes } : a
          )
        );
        if (searchResults) {
          setSearchResults((prev) =>
            prev.map((a) =>
              a._id === article._id ? { ...a, likes: res.data.likes } : a
            )
          );
        }
      } else {
        // NewsAPI article
        if (externalLikes.includes(article.url)) {
          await axios.delete(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { data: { url: article.url }, headers: { 'x-auth-token': token } });
          setExternalLikes((prev) => prev.filter((u) => u !== article.url));
        } else {
          await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { url: article.url }, { headers: { 'x-auth-token': token } });
          setExternalLikes((prev) => [...prev, article.url]);
        }
      }
    } catch (err) {
      toast.error('Failed to like article');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="modern-bg min-h-screen py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 pt-20">
        {/* Main Feed Card */}
        <div className="bg-white rounded-2xl shadow-2xl p-8 border border-gray-100 mb-8">
          <h1 className="text-3xl font-extrabold text-primary-700 mb-8 tracking-tight drop-shadow">{t('Your Personalized News Feed')}</h1>
          {/* Search Bar */}
          <form onSubmit={handleSearch} className="mb-6 flex flex-col md:flex-row md:items-end gap-3 flex-wrap">
            <input
              value={query}
              onChange={e => setQuery(e.target.value)}
              placeholder={t('Search news...')}
              className="flex-1 min-w-[180px] rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
            />
            <select
              value={category}
              onChange={e => setCategory(e.target.value)}
              className="min-w-[150px] rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
            >
              <option value="">{t('All Categories')}</option>
              <option value="technology">{t('Technology')}</option>
              <option value="science">{t('Science')}</option>
              <option value="business">{t('Business')}</option>
              <option value="health">{t('Health')}</option>
              <option value="entertainment">{t('Entertainment')}</option>
              <option value="sports">{t('Sports')}</option>
              <option value="politics">{t('Politics')}</option>
              <option value="education">{t('Education')}</option>
            </select>
            <select
              value={mediaType}
              onChange={e => setMediaType(e.target.value)}
              className="min-w-[120px] rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
            >
              <option value="">{t('All Media')}</option>
              <option value="text">{t('Text')}</option>
              <option value="image">{t('Image')}</option>
              <option value="video">{t('Video')}</option>
              <option value="podcast">{t('Podcast')}</option>
            </select>
            <DatePicker
              selected={startDate}
              onChange={date => setStartDate(date)}
              selectsStart
              startDate={startDate}
              endDate={endDate}
              placeholderText={t('Start Date')}
              className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 min-w-[120px]"
              dateFormat="yyyy-MM-dd"
              isClearable
            />
            <DatePicker
              selected={endDate}
              onChange={date => setEndDate(date)}
              selectsEnd
              startDate={startDate}
              endDate={endDate}
              minDate={startDate}
              placeholderText={t('End Date')}
              className="rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 min-w-[120px]"
              dateFormat="yyyy-MM-dd"
              isClearable
            />
            <button
              type="submit"
              className="px-4 py-2 rounded-md bg-primary-600 text-white font-medium hover:bg-primary-700"
              disabled={searching}
            >
              {searching ? t('Searching...') : t('Search')}
            </button>
          </form>

          {/* Trending Horizontal Scroll Bar */}
          <div className="mb-8">
            <h2 className="text-xl font-bold text-primary-700 mb-4 flex items-center"><FireIcon className="h-6 w-6 mr-2 text-orange-500" /> {t('Trending')}</h2>
            {trending.length === 0 ? (
              <p className="text-gray-500">{t('No trending topics right now.')}</p>
            ) : (
              <div className="flex overflow-x-auto gap-4 pb-2 hide-scrollbar">
                {trending.map((article, idx) => (
                  <div key={article._id || article.url || idx} className="min-w-[320px] max-w-xs bg-gradient-to-br from-blue-50 to-purple-50 rounded-xl shadow-md border border-blue-100 flex-shrink-0 hover:shadow-lg transition-shadow duration-200">
                    {article.urlToImage || article.imageUrl ? (
                      <img src={article.urlToImage || article.imageUrl} alt={article.title} className="w-full h-32 object-cover rounded-t-xl" />
                    ) : (
                      <div className="w-full h-32 flex items-center justify-center text-4xl text-gray-300">📰</div>
                    )}
                    <div className="p-4 flex flex-col gap-2">
                      <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-semibold bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow">
                        {article.category || article.source?.name || 'Trending'}
                      </span>
                      <h3 className="text-base font-bold text-gray-900 line-clamp-2 mb-1">{article.title}</h3>
                      <span className="text-xs text-gray-400">{article.publishedAt ? new Date(article.publishedAt).toLocaleDateString() : ''}</span>
                      {article.source?.name && <span className="text-xs text-blue-700 font-semibold">{article.source.name}</span>}
                      <a href={article.url} target="_blank" rel="noopener noreferrer" className="mt-2 text-xs text-primary-600 hover:underline font-semibold">Read</a>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Show search results if searching, else show feed */}
          {searchResults ? (
            <div className="mb-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-2">{t('Search Results')}</h2>
              {searchSummary && (
                <div className="mb-4 p-4 bg-blue-50 rounded-md">
                  <h4 className="font-medium text-blue-900 mb-2">{t('AI Summary')}:</h4>
                  <p className="text-blue-800">{searchSummary}</p>
                </div>
              )}
              <div className="space-y-6">
                {searchResults.length === 0 ? (
                  <p className="text-gray-500">{t('No articles found.')}</p>
                ) : (
                  searchResults.map((article) => (
                    <div
                      key={article._id || article.url}
                      className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
                    >
                      {article.imageUrl && (
                        <img
                          src={article.imageUrl}
                          alt={article.title}
                          className="w-full h-48 object-cover"
                        />
                      )}
                      {article.videoUrl && (
                        <div className="my-4">
                          <iframe
                            width="100%"
                            height="315"
                            src={article.videoUrl}
                            title="Video"
                            frameBorder="0"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                          ></iframe>
                        </div>
                      )}
                      {article.podcastUrl && (
                        <div className="my-4">
                          <iframe
                            src={article.podcastUrl}
                            width="100%"
                            height="152"
                            frameBorder="0"
                            allow="autoplay; clipboard-write; encrypted-media; picture-in-picture"
                            allowFullScreen
                            title="Podcast"
                          ></iframe>
                        </div>
                      )}
                      <div className="p-6">
                        <div className="flex items-center justify-between mb-4">
                          <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-medium bg-primary-100 text-primary-800">
                            {article.category}
                          </span>
                          <div className="flex items-center gap-2">
                            <button
                              onClick={() => handleLikeArticle(article)}
                              className={`text-sm flex items-center gap-1 ${article._id ? (userId && article.likes && article.likes.includes(userId) ? 'text-red-500' : 'text-gray-400') : (externalLikes.includes(article.url) ? 'text-red-500' : 'text-gray-400')} hover:text-red-600`}
                              title={article._id ? (userId && article.likes && article.likes.includes(userId) ? 'Unlike' : 'Like') : (externalLikes.includes(article.url) ? 'Unlike' : 'Like')}
                            >
                              <HeartIcon className="h-5 w-5" />
                              {article._id ? (article.likes ? article.likes.length : 0) : ''}
                            </button>
                            <button
                              onClick={() => handleSaveArticle(article)}
                              className="text-gray-400 hover:text-primary-600"
                            >
                              <BookmarkIcon className="h-6 w-6" />
                            </button>
                          </div>
                        </div>
                        <h3 className="text-xl font-semibold text-gray-900 mb-2">
                          {article._id ? (
                            <Link to={`/article/${article._id}`} className="hover:text-primary-600">{article.title}</Link>
                          ) : (
                            <Link to="/external-article" state={{ article }} className="hover:text-primary-600">{article.title}</Link>
                          )}
                        </h3>
                        <p className="text-gray-600 mb-4">{article.summary}</p>
                        <span className="text-sm text-gray-500">
                          {new Date(article.publishedAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              {/* Articles Feed */}
              <div className="lg:col-span-2">
                <h2 className="text-3xl font-extrabold text-primary-700 mb-6 tracking-tight drop-shadow">{t('Your Personalized Feed')}</h2>
                <div className="space-y-8">
                  {articles.map((article) => (
                    <div
                      key={article._id || article.url}
                      className="relative group bg-white/70 backdrop-blur-lg rounded-3xl shadow-2xl border border-gray-100 overflow-hidden transition-transform duration-300 hover:-translate-y-1 hover:shadow-3xl hover:border-blue-400"
                      style={{ borderLeft: `8px solid #6366f1` }}
                    >
                      <div className="flex flex-col md:flex-row">
                        <div className="flex-shrink-0 w-full md:w-56 h-48 md:h-auto bg-gradient-to-br from-blue-100 to-purple-100 flex items-center justify-center">
                          {article.imageUrl ? (
                            <img
                              src={article.imageUrl}
                              alt={article.title}
                              className="w-full h-full object-cover rounded-t-3xl md:rounded-l-3xl md:rounded-t-none"
                            />
                          ) : (
                            <div className="w-full h-full flex items-center justify-center text-5xl text-gray-300">
                              <span role="img" aria-label="news">📰</span>
                            </div>
                          )}
                        </div>
                        <div className="flex-1 p-6 flex flex-col justify-between">
                          <div>
                            <div className="flex items-center gap-3 mb-2">
                              <span className="inline-flex items-center px-3 py-0.5 rounded-full text-xs font-semibold bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow">
                                {article.category || article.source?.name || 'General'}
                              </span>
                              {article.videoUrl && <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-pink-100 text-pink-700"><svg className="h-4 w-4 mr-1" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M4 6v12a2 2 0 002 2h8a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2z" /></svg>Video</span>}
                              {article.podcastUrl && <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-700"><svg className="h-4 w-4 mr-1" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="3" /><path d="M19.4 15A7.963 7.963 0 0020 12c0-4.418-3.582-8-8-8S4 7.582 4 12c0 1.042.2 2.04.56 2.95" /></svg>Podcast</span>}
                            </div>
                            <h3 className="text-2xl font-extrabold text-gray-900 mb-2 group-hover:text-primary-700 transition-colors duration-200">
                              {article._id ? (
                                <Link to={`/article/${article._id}`}>{article.title}</Link>
                              ) : (
                                <Link to="/external-article" state={{ article }}>{article.title}</Link>
                              )}
                            </h3>
                            <p className="text-gray-700 mb-4 text-lg leading-relaxed line-clamp-3">{article.summary || article.description}</p>
                          </div>
                          <div className="flex items-center justify-between mt-4">
                            <div className="flex items-center gap-3 text-xs text-gray-500">
                              {article.source?.name && <span className="font-semibold text-blue-700">{article.source.name}</span>}
                              <span>{article.publishedAt ? new Date(article.publishedAt).toLocaleDateString() : ''}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <button
                                onClick={() => handleLikeArticle(article)}
                                className={`text-sm flex items-center gap-1 ${article._id ? (userId && article.likes && article.likes.includes(userId) ? 'text-red-500' : 'text-gray-400') : (externalLikes.includes(article.url) ? 'text-red-500' : 'text-gray-400')} hover:text-red-600`}
                                title={article._id ? (userId && article.likes && article.likes.includes(userId) ? 'Unlike' : 'Like') : (externalLikes.includes(article.url) ? 'Unlike' : 'Like')}
                              >
                                <HeartIcon className="h-5 w-5" />
                                {article._id ? (article.likes ? article.likes.length : 0) : ''}
                              </button>
                              <button
                                onClick={() => handleSaveArticle(article)}
                                className="text-gray-400 hover:text-primary-600"
                              >
                                <BookmarkIcon className="h-6 w-6" />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* AI Assistant Panel */}
              <div className="lg:col-span-1">
                <div className="bg-white rounded-lg shadow-md p-6 sticky top-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">AI Assistant</h3>
                  {selectedArticle ? (
                    <div>
                      <h4 className="font-medium text-gray-900 mb-2">{selectedArticle.title}</h4>
                      <form onSubmit={handleAskQuestion} className="space-y-4">
                        <div>
                          <label htmlFor="question" className="block text-sm font-medium text-gray-700">
                            Ask a question about this article
                          </label>
                          <textarea
                            id="question"
                            rows={3}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
                            value={question}
                            onChange={(e) => setQuestion(e.target.value)}
                            placeholder="What would you like to know?"
                          />
                        </div>
                        <button
                          type="submit"
                          className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                        >
                          Ask AI
                        </button>
                      </form>
                      {answer && (
                        <div className="mt-4 p-4 bg-gray-50 rounded-md">
                          <h5 className="font-medium text-gray-900 mb-2">AI Response:</h5>
                          <p className="text-gray-600">{answer}</p>
                        </div>
                      )}
                    </div>
                  ) : (
                    <p className="text-gray-500">
                      Select an article to ask questions about it using our AI assistant.
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
