import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { BookmarkIcon, ChatBubbleLeftIcon, HeartIcon, ChatBubbleOvalLeftEllipsisIcon, FireIcon, XMarkIcon, ShareIcon } from '@heroicons/react/24/outline';
import { io } from 'socket.io-client';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useTranslation } from 'react-i18next';
import ReactMarkdown from 'react-markdown';
import './HomeModern.css';

export default function Home() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [aiLoading, setAiLoading] = useState(false);
  const [dailyBriefing, setDailyBriefing] = useState('');
  const [briefingLoading, setBriefingLoading] = useState(true);
  // Search state
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [searchSummary, setSearchSummary] = useState('');
  const [searching, setSearching] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [userId, setUserId] = useState(null);
  const [trending, setTrending] = useState([]);
  const [category, setCategory] = useState('');
  const [mediaType, setMediaType] = useState('');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const { t } = useTranslation();
  const [externalLikes, setExternalLikes] = useState([]); // NewsAPI liked URLs
  const [userProfile, setUserProfile] = useState(null);
  const navigate = useNavigate();
  const [trendingPanelOpen, setTrendingPanelOpen] = useState(false);
  const [selectedTrending, setSelectedTrending] = useState(null);
  const [showAllTrending, setShowAllTrending] = useState(false);
  const [nextPage, setNextPage] = useState(null);
  const [loadingMore, setLoadingMore] = useState(false);
  const observer = useRef();
  const lastArticleRef = useCallback(node => {
    if (loadingMore) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new window.IntersectionObserver(entries => {
      if (entries[0].isIntersecting && nextPage) {
        loadMoreArticles();
      }
    });
    if (node) observer.current.observe(node);
  }, [loadingMore, nextPage]);

  useEffect(() => {
    fetchArticles();
    // Fetch external likes and user profile
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/profile`, {
          headers: { 'x-auth-token': token },
        });
        setExternalLikes(res.data.externalLikes || []);
        setUserProfile(res.data);
      } catch (err) {
        // ignore
      }
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const socket = io('http://localhost:5000'); // or your backend URL

    socket.on('new-article', (article) => {
      setArticles((prev) => [article, ...prev]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  useEffect(() => {
    const fetchNotifications = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/user/notifications`, {
        headers: { 'x-auth-token': token }
      });
      setNotifications(res.data);
    };
    fetchNotifications();
  }, []);

  useEffect(() => {
    // Get user ID from token (assumes JWT with sub or id field)
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUserId(payload.id || payload.sub || null);
      } catch (e) {
        setUserId(null);
      }
    }
  }, []);

  useEffect(() => {
    // Fetch trending articles
    const fetchTrending = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/trending`, {
          headers: { 'x-auth-token': token },
        });
        setTrending(res.data);
      } catch (err) {
        // Optionally toast error
      }
    };
    fetchTrending();
    fetchDailyBriefing();
  }, []);

  const fetchDailyBriefing = async () => {
    setBriefingLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/ai/daily-briefing`, {
        headers: { 'x-auth-token': token },
      });
      setDailyBriefing(res.data.briefing);
    } catch (err) {
      setDailyBriefing('Failed to load the daily briefing. Please try again later.');
    } finally {
      setBriefingLoading(false);
    }
  };

  const fetchArticles = async (reset = false) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setLoading(false);
        return;
      }
      setLoading(true);
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed`, {
        headers: { 'x-auth-token': token },
      });
      setArticles(res.data.articles || []);
      setNextPage(res.data.nextPage || null);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch articles');
      setLoading(false);
    }
  };

  const loadMoreArticles = async () => {
    if (!nextPage) return;
    setLoadingMore(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/news/feed?nextPage=${encodeURIComponent(nextPage)}`, {
        headers: { 'x-auth-token': token },
      });
      setArticles(prev => [...prev, ...(res.data.articles || [])]);
      setNextPage(res.data.nextPage || null);
    } catch (err) {
      toast.error('Failed to load more articles');
    } finally {
      setLoadingMore(false);
    }
  };

  const handleSaveArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/save`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        toast.success('Article saved successfully');
      } else {
        // NewsAPI/newsdata.io article
        const url = article.url || article.link;
        console.log('Sending bookmark for external article with url:', url);
        if (!url || typeof url !== 'string' || !url.startsWith('http')) {
          toast.error('No valid URL for this article.');
          return;
        }
        await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/bookmark`, { url }, { headers: { 'x-auth-token': token } });
        toast.success('Bookmarked!');
      }
    } catch (err) {
      toast.error('Failed to save article');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;
    setAiLoading(true);

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/qa`,
        {
          question,
          context: selectedArticle.content,
        },
        {
          headers: {
            'x-auth-token': token,
          },
        }
      );
      setAnswer(res.data.answer);
    } catch (err) {
      toast.error('Failed to get answer');
    } finally {
      setAiLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearching(true);
    try {
      const token = localStorage.getItem('token');
      const params = new URLSearchParams({ q: query });
      if (category) params.append('category', category);
      if (mediaType) params.append('mediaType', mediaType);
      if (startDate) params.append('startDate', startDate.toISOString());
      if (endDate) params.append('endDate', endDate.toISOString());
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/news/search?${params.toString()}`,
        { headers: { 'x-auth-token': token } }
      );
      setSearchResults(res.data.articles);
      setSearchSummary(res.data.summary);
    } catch (err) {
      toast.error('Failed to search');
    } finally {
      setSearching(false);
    }
  };

  const handleBackToFeed = () => {
    setSearchResults(null);
    setSearchSummary('');
    setQuery('');
  };

  const handleLikeArticle = async (article) => {
    try {
      const token = localStorage.getItem('token');
      if (article._id) {
        const res = await axios.post(
          `${process.env.REACT_APP_API_URL}/api/news/article/${article._id}/like`,
          {},
          { headers: { 'x-auth-token': token } }
        );
        setArticles((prev) =>
          prev.map((a) =>
            a._id === article._id ? { ...a, likes: res.data.likes } : a
          )
        );
        if (searchResults) {
          setSearchResults((prev) =>
            prev.map((a) =>
              a._id === article._id ? { ...a, likes: res.data.likes } : a
            )
          );
        }
      } else {
        // NewsAPI/newsdata.io article
        const url = article.url || article.link;
        console.log('Sending like for external article with url:', url);
        if (!url || typeof url !== 'string' || !url.startsWith('http')) {
          toast.error('No valid URL for this article.');
          return;
        }
        if (externalLikes.includes(url)) {
          await axios.delete(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { data: { url }, headers: { 'x-auth-token': token } });
          setExternalLikes((prev) => prev.filter((u) => u !== url));
        } else {
          await axios.post(`${process.env.REACT_APP_API_URL}/api/news/external/like`, { url }, { headers: { 'x-auth-token': token } });
          setExternalLikes((prev) => [...prev, url]);
        }
      }
    } catch (err) {
      toast.error('Failed to like article');
    }
  };

  // Helper for greeting
  function getGreeting() {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 18) return 'Good afternoon';
    return 'Good evening';
  }

  // Helper for sharing (copy link)
  const handleShare = (url) => {
    if (navigator.clipboard && url) {
      navigator.clipboard.writeText(url);
      toast.success('Link copied!');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div>
      {/* Personalized Greeting and Profile - Removed for brevity, assumed to be styled correctly */}

      {/* Main Content Area */}
      <div className="lg:grid lg:grid-cols-3 lg:gap-8">
        {/* Main Feed Column */}
        <div className="lg:col-span-2">
          {/* Search Results */}
          {searchResults ? (
            <div className="bg-white dark:bg-slate-800 rounded-lg shadow-md p-6">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold text-gray-800 dark:text-gray-100">{t('Search Results')}</h2>
                <button onClick={handleBackToFeed} className="text-primary-600 dark:text-primary-300 hover:underline">{t('Back to Feed')}</button>
              </div>
              {searchSummary && (
                <div className="mb-6 p-4 bg-blue-50 dark:bg-slate-700 rounded-md">
                  <h4 className="font-medium text-blue-900 dark:text-blue-200 mb-1">{t('AI Summary')}:</h4>
                  <p className="text-blue-800 dark:text-blue-300">{searchSummary}</p>
                </div>
              )}
              <div className="space-y-6">
                {searchResults.length > 0 ? (
                  searchResults.map((article, idx) => (
                    // Re-using the article card component styling
                    <ArticleCard key={article.url || idx} article={article} />
                  ))
                ) : (
                  <p className="text-gray-500 dark:text-gray-400">{t('No articles found for your query.')}</p>
                )}
              </div>
            </div>
          ) : (
            /* Personalized Feed */
            <div className="space-y-8">
              {articles.map((article, idx) => (
                 <ArticleCard key={article.url || idx} article={article} isLast={idx === articles.length - 1} lastArticleRef={lastArticleRef}/>
              ))}
            </div>
          )}

          {loadingMore && (
            <div className="flex justify-center items-center py-6">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
            </div>
          )}
          {!loadingMore && !nextPage && articles.length > 0 && (
             <div className="text-center py-8">
                <p className="text-gray-500 dark:text-gray-400 italic">{t("You've reached the end of your feed.")}</p>
            </div>
          )}
        </div>

        {/* Right Sidebar Column */}
        <div className="lg:col-span-1 space-y-8">
           <div className="sticky top-24">
              {/* AI Assistant Panel */}
              <div className="bg-white dark:bg-slate-800 rounded-lg shadow-md p-6 border border-gray-200 dark:border-slate-700">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-bold text-gray-800 dark:text-gray-100 flex items-center">
                    <ChatBubbleOvalLeftEllipsisIcon className="w-6 h-6 mr-2" />
                    {t('ai_assistant')}
                  </h3>
                   {/* Refresh button remains the same */}
                </div>
                <div className="text-sm text-gray-700 dark:text-gray-300 prose prose-sm max-w-none prose-p:my-1 prose-headings:text-gray-700 dark:prose-headings:text-gray-300">
                  {briefingLoading ? (
                    <p>{t('loading_briefing')}</p>
                  ) : (
                    <ReactMarkdown>{dailyBriefing}</ReactMarkdown>
                  )}
                </div>
              </div>

              {/* Trending Panel */}
              <div className="mt-8 bg-white dark:bg-slate-800 rounded-lg shadow-md p-6 border border-gray-200 dark:border-slate-700">
                <h3 className="text-lg font-bold text-gray-800 dark:text-gray-100 flex items-center mb-4">
                    <FireIcon className="w-6 h-6 mr-2 text-orange-500" />
                    {t('Trending')}
                </h3>
                <div className="space-y-4">
                    {trending.slice(0, 5).map((item) => (
                        <div key={item.url || item.link} className="group">
                             <a href={item.url || item.link} target="_blank" rel="noopener noreferrer">
                                <p className="font-semibold text-gray-800 dark:text-gray-200 group-hover:text-primary-600 dark:group-hover:text-primary-400 text-sm">{item.title}</p>
                                <p className="text-xs text-gray-500 dark:text-gray-400">{item.source?.name}</p>
                            </a>
                        </div>
                    ))}
                </div>
              </div>
           </div>
        </div>
      </div>
    </div>
  );
}

// Create a reusable ArticleCard component
const ArticleCard = ({ article, isLast, lastArticleRef }) => {
    // ... (logic from handleLikeArticle, etc. would go here, simplified for now)
    return (
        <div ref={isLast ? lastArticleRef : null} className="bg-white dark:bg-slate-800 rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow duration-300 flex flex-col md:flex-row">
            { (article.image_url || article.imageUrl || article.urlToImage) &&
                <img src={article.image_url || article.imageUrl || article.urlToImage} alt={article.title} className="w-full md:w-1/3 h-48 md:h-auto object-cover"/>
            }
            <div className="p-6 flex flex-col justify-between">
                <div>
                    <span className="text-xs font-semibold text-primary-600 dark:text-primary-400 uppercase">{article.category || 'News'}</span>
                    <h3 className="text-xl font-bold text-gray-900 dark:text-white mt-1 mb-2">
                        <Link to={article._id ? `/article/${article._id}` : '/external-article'} state={{ article }} className="hover:text-primary-600 dark:hover:text-primary-400 transition-colors">
                            {article.title}
                        </Link>
                    </h3>
                    <p className="text-gray-600 dark:text-gray-300 line-clamp-3">{article.summary || article.description}</p>
                </div>
                <div className="mt-4 flex justify-between items-center">
                    <p className="text-xs text-gray-500 dark:text-gray-400">
                        {article.source?.name || 'Unknown Source'} &bull; {new Date(article.publishedAt).toLocaleDateString()}
                    </p>
                    {/* Action buttons would be here */}
                </div>
            </div>
        </div>
    )
}
